// Dashboard functions
function updateDashboardStats() {
  const totalOrders = orders.length
  const totalRevenue = orders.reduce((sum, order) => sum + order.total, 0)
  const pendingOrders = orders.filter((order) => order.status === "pending").length
  const completedOrders = orders.filter((order) => order.status === "completed").length

  document.getElementById("totalOrders").textContent = totalOrders
  document.getElementById("totalRevenue").textContent = `MMK ${Math.round(totalRevenue).toLocaleString()}`
  document.getElementById("pendingOrders").textContent = pendingOrders
  document.getElementById("completedOrders").textContent = completedOrders
}

function loadRecentOrders() {
  const recentOrdersContainer = document.getElementById("recentOrders")
  const recentOrders = orders.slice(-5).reverse()

  if (recentOrders.length === 0) {
    recentOrdersContainer.innerHTML = '<p style="color: #7f8c8d; text-align: center;">No recent orders</p>'
    return
  }

  recentOrdersContainer.innerHTML = ""
  recentOrders.forEach((order) => {
    const orderElement = document.createElement("div")
    orderElement.className = "recent-order-item"
    orderElement.innerHTML = `
            <div class="order-info">
                <h4>Order #${order.id}</h4>
                <p>${order.customer.name} - ${order.items.length} items</p>
            </div>
            <div>
                <span class="order-status status-${order.status}">${order.status.charAt(0).toUpperCase() + order.status.slice(1)}</span>
            </div>
        `
    recentOrdersContainer.appendChild(orderElement)
  })
}

function loadPopularItems() {
  const popularItemsContainer = document.getElementById("popularItems")

  // Calculate item popularity from orders
  const itemCounts = {}
  orders.forEach((order) => {
    order.items.forEach((item) => {
      itemCounts[item.name] = (itemCounts[item.name] || 0) + item.quantity
    })
  })

  const popularItems = Object.entries(itemCounts)
    .sort(([, a], [, b]) => b - a)
    .slice(0, 5)

  if (popularItems.length === 0) {
    popularItemsContainer.innerHTML = '<p style="color: #7f8c8d; text-align: center;">No order data available</p>'
    return
  }

  popularItemsContainer.innerHTML = ""
  popularItems.forEach(([itemName, count]) => {
    const itemElement = document.createElement("div")
    itemElement.className = "popular-item"
    itemElement.innerHTML = `
            <div class="item-info">
                <h4>${itemName}</h4>
                <p>Ordered ${count} times</p>
            </div>
            <div style="color: #8b4513; font-weight: 600;">${count}</div>
        `
    popularItemsContainer.appendChild(itemElement)
  })
}

// Orders management
function loadOrdersTable() {
  const tbody = document.getElementById("ordersTableBody")
  let filteredOrders = [...orders]

  // Apply filters
  if (currentOrderFilter !== "all") {
    filteredOrders = filteredOrders.filter((order) => order.status === currentOrderFilter)
  }

  if (currentDateFilter) {
    filteredOrders = filteredOrders.filter((order) => {
      const orderDate = new Date(order.timestamp).toISOString().split("T")[0]
      return orderDate === currentDateFilter
    })
  }

  // Sort by newest first
  filteredOrders.sort((a, b) => new Date(b.timestamp) - new Date(a.timestamp))

  tbody.innerHTML = ""

  if (filteredOrders.length === 0) {
    tbody.innerHTML = `
            <tr>
                <td colspan="8" style="text-align: center; color: #7f8c8d; padding: 2rem;">
                    No orders found
                </td>
            </tr>
        `
    return
  }

  filteredOrders.forEach((order) => {
    const row = document.createElement("tr")
    row.innerHTML = `
            <td>#${order.id}</td>
            <td>
                <div>
                    <strong>${order.customer.name}</strong><br>
                    <small>${order.customer.phone}</small>
                </div>
            </td>
            <td>${order.items.length} items</td>
            <td>MMK ${Math.round(order.total).toLocaleString()}</td>
            <td>${order.orderType === "cod" ? "Delivery" : "Pickup"}</td>
            <td>
                <span class="order-status status-${order.status}">
                    ${order.status.charAt(0).toUpperCase() + order.status.slice(1)}
                </span>
            </td>
            <td>${new Date(order.timestamp).toLocaleDateString()}</td>
            <td>
                <div class="order-actions">
                    <button class="btn-sm btn-info" onclick="showOrderDetails(${order.id})">
                        <i class="fas fa-eye"></i>
                    </button>
                    ${order.status === "pending"
        ? `<button class="btn-sm btn-success" onclick="updateOrderStatus(${order.id}, 'preparing')">
                               <i class="fas fa-play"></i>
                           </button>`
        : ""
      }
                    ${order.status === "preparing"
        ? `<button class="btn-sm btn-warning" onclick="updateOrderStatus(${order.id}, 'ready')">
                               <i class="fas fa-check"></i>
                           </button>`
        : ""
      }
                    ${order.status === "ready"
        ? `<button class="btn-sm btn-success" onclick="updateOrderStatus(${order.id}, 'completed')">
                               <i class="fas fa-check-double"></i>
                           </button>`
        : ""
      }
                    ${order.status !== "completed" && order.status !== "cancelled"
        ? `<button class="btn-sm btn-danger" onclick="updateOrderStatus(${order.id}, 'cancelled')">
                               <i class="fas fa-times"></i>
                           </button>`
        : ""
      }
                </div>
            </td>
        `
    tbody.appendChild(row)
  })
}

function filterOrders() {
  currentOrderFilter = document.getElementById("orderStatusFilter").value
  currentDateFilter = document.getElementById("orderDateFilter").value
  loadOrdersTable()
}

function updateOrderStatus(orderId, newStatus) {
  const order = orders.find((o) => o.id === orderId)
  if (order) {
    order.status = newStatus
    localStorage.setItem("orders", JSON.stringify(orders))
    loadOrdersTable()
    updateDashboardStats()
    loadRecentOrders()
    showNotification(`Order #${orderId} status updated to ${newStatus}`)
  }
}

