function closeOrderDetails() {
  document.getElementById("orderDetailsModal").style.display = "none"
}

// Menu management
function loadAdminMenu() {
  const menuGrid = document.getElementById("adminMenuGrid")
  menuGrid.innerHTML = ""

  let filteredItems = [...menuItems]

  const categoryFilter = document.getElementById("categoryFilter")?.value || "all"
  const stockFilter = document.getElementById("stockFilter")?.value || "all"

  if (categoryFilter !== "all") {
    filteredItems = filteredItems.filter((item) => item.category === categoryFilter)
  }

  if (stockFilter === "instock") {
    filteredItems = filteredItems.filter((item) => item.inStock === true)
  } else if (stockFilter === "outofstock") {
    filteredItems = filteredItems.filter((item) => item.inStock === false)
  }

  filteredItems.forEach((item) => {
    const menuItemElement = createAdminMenuItemElement(item)
    menuGrid.appendChild(menuItemElement)
  })
}

function filterAdminMenu() {
  loadAdminMenu()
}

function createAdminMenuItemElement(item) {
  const div = document.createElement("div")
  div.className = "admin-menu-item"

  div.innerHTML = `
        <img src="${item.image}" alt="${item.name}">
        <div class="admin-menu-item-content">
            <h3>${item.name}</h3>
            <p>${item.description}</p>
            <div class="admin-menu-item-footer">
                <!-- Updated currency display to MMK without decimals -->
                <span class="price">MMK ${Math.round(item.price).toLocaleString()}</span>
                <div class="stock-toggle">
                    <input type="checkbox" ${item.inStock ? "checked" : ""} 
                           onchange="toggleItemStock(${item.id}, this.checked)">
                    <label>In Stock</label>
                </div>
            </div>
            <div class="admin-menu-actions">
                <button class="btn-sm btn-edit" onclick="editMenuItem(${item.id})">
                    <i class="fas fa-edit"></i> Edit
                </button>
                <button class="btn-sm btn-delete" onclick="deleteMenuItem(${item.id})">
                    <i class="fas fa-trash"></i> Delete
                </button>
            </div>
        </div>
    `

  return div
}

function showAddItemModal() {
  document.getElementById("menuItemModalTitle").textContent = "Add New Item"
  document.getElementById("editItemId").value = ""
  document.getElementById("itemName").value = ""
  document.getElementById("itemDescription").value = ""
  document.getElementById("itemPrice").value = ""
  document.getElementById("itemCategory").value = ""
  document.getElementById("itemImage").value = ""
  document.getElementById("itemInStock").checked = true
  document.getElementById("menuItemModal").style.display = "block"
}

function editMenuItem(itemId) {
  const item = menuItems.find((i) => i.id === itemId)
  if (!item) return

  document.getElementById("menuItemModalTitle").textContent = "Edit Menu Item"
  document.getElementById("editItemId").value = item.id
  document.getElementById("itemName").value = item.name
  document.getElementById("itemDescription").value = item.description
  document.getElementById("itemPrice").value = item.price
  document.getElementById("itemCategory").value = item.category
  document.getElementById("itemImage").value = item.image
  document.getElementById("itemInStock").checked = item.inStock
  document.getElementById("menuItemModal").style.display = "block"
}

function saveMenuItem(event) {
  event.preventDefault()

  const itemId = document.getElementById("editItemId").value
  const itemData = {
    name: document.getElementById("itemName").value,
    description: document.getElementById("itemDescription").value,
    price: Number.parseFloat(document.getElementById("itemPrice").value),
    category: document.getElementById("itemCategory").value,
    image: document.getElementById("itemImage").value || "/placeholder.svg?height=200&width=300",
    inStock: document.getElementById("itemInStock").checked,
  }

  if (itemId) {
    // Edit existing item
    const itemIndex = menuItems.findIndex((i) => i.id === Number.parseInt(itemId))
    if (itemIndex !== -1) {
      menuItems[itemIndex] = { ...menuItems[itemIndex], ...itemData }
      showNotification("Menu item updated successfully!")
    }
  } else {
    // Add new item
    const newItem = {
      id: Date.now(),
      ...itemData,
    }
    menuItems.push(newItem)
    showNotification("Menu item added successfully!")
  }

  // Update the main website's menu items if possible
  if (window.opener && window.opener.menuItems) {
    window.opener.menuItems = [...menuItems]
    if (window.opener.loadMenu) {
      window.opener.loadMenu()
    }
  }

  closeMenuItemModal()
  loadAdminMenu()
}

function deleteMenuItem(itemId) {
  if (confirm("Are you sure you want to delete this menu item?")) {
    menuItems = menuItems.filter((item) => item.id !== itemId)

    // Update the main website's menu items if possible
    if (window.opener && window.opener.menuItems) {
      window.opener.menuItems = [...menuItems]
      if (window.opener.loadMenu) {
        window.opener.loadMenu()
      }
    }

    loadAdminMenu()
    showNotification("Menu item deleted successfully!")
  }
}

function toggleItemStock(itemId, inStock) {
  const item = menuItems.find((i) => i.id === itemId)
  if (item) {
    item.inStock = inStock

    // Update the main website's menu items if possible
    if (window.opener && window.opener.menuItems) {
      window.opener.menuItems = [...menuItems]
      if (window.opener.loadMenu) {
        window.opener.loadMenu()
      }
    }

    showNotification(`${item.name} ${inStock ? "is now in stock" : "is now out of stock"}`)
  }
}

function closeMenuItemModal() {
  document.getElementById("menuItemModal").style.display = "none"
}

// Analytics
function setDefaultDates() {
  const today = new Date()
  const lastWeek = new Date(today.getTime() - 7 * 24 * 60 * 60 * 1000)

  document.getElementById("analyticsStartDate").value = lastWeek.toISOString().split("T")[0]
  document.getElementById("analyticsEndDate").value = today.toISOString().split("T")[0]
}

function updateAnalytics() {
  const startDate = document.getElementById("analyticsStartDate").value
  const endDate = document.getElementById("analyticsEndDate").value

  if (!startDate || !endDate) {
    showNotification("Please select both start and end dates")
    return
  }

  // Filter orders by date range
  const filteredOrders = orders.filter((order) => {
    const orderDate = new Date(order.timestamp).toISOString().split("T")[0]
    return orderDate >= startDate && orderDate <= endDate
  })

  // Update all analytics
  updateSalesChart(filteredOrders)
  updateOrderTypesChart(filteredOrders)
  updatePopularItemsAnalytics(filteredOrders)
  updateRevenueChart(filteredOrders)
  updatePerformanceMetrics(filteredOrders, startDate, endDate)

  showNotification(`Analytics updated for ${filteredOrders.length} orders`)
}

function updateSalesChart(orders) {
  const chartContainer = document.getElementById("salesChart")
  const totalRevenue = orders.reduce((sum, order) => sum + order.total, 0)
  const completedOrders = orders.filter((order) => order.status === "completed").length

  chartContainer.innerHTML = `
        <div class="sales-summary">
            <div class="sales-metric">
                <h4>Total Orders</h4>
                <div class="metric-value">${orders.length}</div>
            </div>
            <div class="sales-metric">
                <h4>Total Revenue</h4>
                <div class="metric-value">MMK ${Math.round(totalRevenue).toLocaleString()}</div>
            </div>
            <div class="sales-metric">
                <h4>Completed</h4>
                <div class="metric-value">${completedOrders}</div>
            </div>
            <div class="sales-metric">
                <h4>Success Rate</h4>
                <div class="metric-value">${orders.length > 0 ? Math.round((completedOrders / orders.length) * 100) : 0}%</div>
            </div>
        </div>
    `
}

function updateOrderTypesChart(orders) {
  const chartContainer = document.getElementById("orderTypesChart")
  const pickupOrders = orders.filter((order) => order.orderType === "pickup").length
  const deliveryOrders = orders.filter((order) => order.orderType === "cod").length
  const total = pickupOrders + deliveryOrders

  const pickupPercentage = total > 0 ? Math.round((pickupOrders / total) * 100) : 0
  const deliveryPercentage = total > 0 ? Math.round((deliveryOrders / total) * 100) : 0

  chartContainer.innerHTML = `
        <div class="order-types-breakdown">
            <div class="order-type-item">
                <div class="order-type-bar">
                    <div class="bar-fill pickup" style="width: ${pickupPercentage}%"></div>
                </div>
                <div class="order-type-info">
                    <span>Pickup: ${pickupOrders} (${pickupPercentage}%)</span>
                </div>
            </div>
            <div class="order-type-item">
                <div class="order-type-bar">
                    <div class="bar-fill delivery" style="width: ${deliveryPercentage}%"></div>
                </div>
                <div class="order-type-info">
                    <span>Delivery: ${deliveryOrders} (${deliveryPercentage}%)</span>
                </div>
            </div>
        </div>
    `
}

function updatePopularItemsAnalytics(orders) {
  const container = document.getElementById("popularItemsAnalytics")

  // Calculate item popularity from orders
  const itemCounts = {}
  orders.forEach((order) => {
    order.items.forEach((item) => {
      if (!itemCounts[item.name]) {
        itemCounts[item.name] = { count: 0, revenue: 0 }
      }
      itemCounts[item.name].count += item.quantity
      itemCounts[item.name].revenue += item.price * item.quantity
    })
  })

  const popularItems = Object.entries(itemCounts)
    .sort(([, a], [, b]) => b.count - a.count)
    .slice(0, 5)

  if (popularItems.length === 0) {
    container.innerHTML = '<p style="color: #7f8c8d; text-align: center; padding: 2rem;">No order data available</p>'
    return
  }

  container.innerHTML = `
        <div class="popular-items-list">
            ${popularItems
      .map(
        ([itemName, data], index) => `
                <div class="popular-item-analytics">
                    <div class="item-rank">#${index + 1}</div>
                    <div class="item-details">
                        <div class="item-name">${itemName}</div>
                        <div class="item-stats">
                            <span>${data.count} sold</span>
                            <span>MMK ${Math.round(data.revenue).toLocaleString()}</span>
                        </div>
                    </div>
                </div>
            `,
      )
      .join("")}
        </div>
    `
}

function updateRevenueChart(orders) {
  const chartContainer = document.getElementById("revenueChart")

  // Group orders by date
  const dailyRevenue = {}
  orders.forEach((order) => {
    const date = new Date(order.timestamp).toISOString().split("T")[0]
    dailyRevenue[date] = (dailyRevenue[date] || 0) + order.total
  })

  const sortedDates = Object.keys(dailyRevenue).sort()
  const maxRevenue = Math.max(...Object.values(dailyRevenue))

  if (sortedDates.length === 0) {
    chartContainer.innerHTML =
      '<p style="color: #7f8c8d; text-align: center; padding: 2rem;">No revenue data available</p>'
    return
  }

  chartContainer.innerHTML = `
        <div class="revenue-chart">
            ${sortedDates
      .map((date) => {
        const revenue = dailyRevenue[date]
        const height = maxRevenue > 0 ? (revenue / maxRevenue) * 100 : 0
        return `
                <div class="revenue-bar-container">
                    <div class="revenue-bar" style="height: ${height}%" title="MMK ${Math.round(revenue).toLocaleString()}"></div>
                    <div class="revenue-date">${new Date(date).toLocaleDateString("en-US", { month: "short", day: "numeric" })}</div>
                </div>
              `
      })
      .join("")}
        </div>
    `
}

function updatePerformanceMetrics(orders, startDate, endDate) {
  const totalRevenue = orders.reduce((sum, order) => sum + order.total, 0)
  const avgOrderValue = orders.length > 0 ? totalRevenue / orders.length : 0

  const daysDiff = Math.ceil((new Date(endDate) - new Date(startDate)) / (1000 * 60 * 60 * 24)) + 1
  const ordersPerDay = daysDiff > 0 ? orders.length / daysDiff : 0

  // Calculate peak hour
  const hourCounts = {}
  orders.forEach((order) => {
    const hour = new Date(order.timestamp).getHours()
    hourCounts[hour] = (hourCounts[hour] || 0) + 1
  })

  const peakHour = Object.keys(hourCounts).reduce((a, b) => (hourCounts[a] > hourCounts[b] ? a : b), "0")

  const completedOrders = orders.filter((order) => order.status === "completed").length
  const conversionRate = orders.length > 0 ? (completedOrders / orders.length) * 100 : 0

  document.getElementById("avgOrderValue").textContent = `MMK ${Math.round(avgOrderValue).toLocaleString()}`
  document.getElementById("ordersPerDay").textContent = Math.round(ordersPerDay * 10) / 10
  document.getElementById("peakHour").textContent = orders.length > 0 ? `${peakHour}:00` : "--:--"
  document.getElementById("conversionRate").textContent = `${Math.round(conversionRate)}%`
}

// Settings
function updateStoreInfo(event) {
  event.preventDefault()
  showNotification("Store information updated successfully!")
}

function updateBusinessHours(event) {
  event.preventDefault()
  showNotification("Business hours updated successfully!")
}

// Utility functions
function showNotification(message) {
  const notification = document.createElement("div")
  notification.style.cssText = `
        position: fixed;
        top: 100px;
        right: 20px;
        background: #8B4513;
        color: white;
        padding: 1rem 1.5rem;
        border-radius: 10px;
        z-index: 3000;
        box-shadow: 0 5px 15px rgba(0,0,0,0.3);
        animation: slideIn 0.3s ease;
    `
  notification.textContent = message

  document.body.appendChild(notification)

  setTimeout(() => {
    notification.remove()
  }, 3000)
}

