// Testimonials
function changeTestimonial(direction) {
  const testimonials = document.querySelectorAll(".testimonial-card")
  testimonials[currentTestimonial].classList.remove("active")

  currentTestimonial += direction

  if (currentTestimonial >= testimonials.length) {
    currentTestimonial = 0
  } else if (currentTestimonial < 0) {
    currentTestimonial = testimonials.length - 1
  }

  testimonials[currentTestimonial].classList.add("active")
}

// Contact form
function submitContactForm(event) {
  event.preventDefault()

  const formData = new FormData(event.target)
  const name = formData.get("name") || event.target.querySelector('input[type="text"]').value
  const email = formData.get("email") || event.target.querySelector('input[type="email"]').value
  const subject = formData.get("subject") || event.target.querySelectorAll('input[type="text"]')[1].value
  const message = formData.get("message") || event.target.querySelector("textarea").value

  // Here you would typically send the data to a server
  console.log("Contact form submitted:", { name, email, subject, message })

  showNotification("Thank you for your message! We will get back to you soon.")
  event.target.reset()
}

// Utility functions
function scrollToSection(sectionId) {
  document.getElementById(sectionId).scrollIntoView({
    behavior: "smooth",
  })
}

function showNotification(message) {
  // Create notification element
  const notification = document.createElement("div")
  notification.style.cssText = `
        position: fixed;
        top: 100px;
        right: 20px;
        background: #8B4513;
        color: white;
        padding: 1rem 1.5rem;
        border-radius: 10px;
        z-index: 3000;
        box-shadow: 0 5px 15px rgba(0,0,0,0.3);
        animation: slideIn 0.3s ease;
    `
  notification.textContent = message

  // Add animation keyframes
  if (!document.querySelector("#notification-styles")) {
    const style = document.createElement("style")
    style.id = "notification-styles"
    style.textContent = `
            @keyframes slideIn {
                from { transform: translateX(100%); opacity: 0; }
                to { transform: translateX(0); opacity: 1; }
            }
        `
    document.head.appendChild(style)
  }

  document.body.appendChild(notification)

  // Remove notification after 3 seconds
  setTimeout(() => {
    notification.remove()
  }, 3000)
}

function saveCartToStorage() {
  localStorage.setItem("cart", JSON.stringify(cart))
}

function loadCartFromStorage() {
  const savedCart = localStorage.getItem("cart")
  if (savedCart) {
    cart = JSON.parse(savedCart)
  }
}

// Make menu items globally accessible for admin
window.menuItems = menuItems
